sys_script_email.script:
(function runMailScript(/* GlideRecord */ current, /* TemplatePrinter */ template,
										   /* Optional EmailOutbound */ email, /* Optional GlideRecord */ email_action,
										   /* Optional GlideRecord */ event) {

	var guestGr = new GlideRecord('x_viph_guest_guest_account');
	guestGr.addEncodedQuery('emailLIKE' + current.getValue('name'));
	guestGr.addActiveQuery();
	guestGr.query();

	if(guestGr.next()){
		template.print('Domain name: ' + current.getValue('name') + '<br>');
		template.print('Requestor: ' + guestGr.responsible_employee.name.getDisplayValue() + ' <br>');
		template.print('Link to the domain: ' + '<a href="http://' + gs.getProperty('instance_name') + '.service-now.com/x_viph_domain_domain.do?sys_id=' + current.getUniqueValue() + '">Domain Record</a><br>');
	}

})(current, template, email, email_action, event);
