sys_script_include.script:
var ViforDOM_KnownFreeEmailDomainHelper = Class.create();
ViforDOM_KnownFreeEmailDomainHelper.prototype = {
	initialize: function() {
		this.restHelper = new ViforDOM_RESTHelper();
	},


	getKnownFreeEmailListOnGithub: function(){
		var r = new sn_ws.RESTMessageV2('x_viph_domain.Github - Known Free Email Domain', 'Get Known Free Email Domain List');
		var resultObj = this.restHelper.executeRestMessage(r, [200]);

		if(resultObj.success){
			return resultObj.body.split('\n');
		} else {
			gs.error("[Github - KnownFreeEmailDomain] - error to receive list!");
			return '';
		}
	},

	type: 'ViforDOM_KnownFreeEmailDomainHelper'
};
