sys_script_include.script:
var ViforDOM_RESTHelper = Class.create();
ViforDOM_RESTHelper.prototype = {
	initialize: function() {
	},

	executeRestMessage: function(restMessage, expectedHttpStatus) {

		var resultObj = {success: false, body: '', httpCode: ''};

		try{
			var response = restMessage.execute();
			var responseBody = response.getBody();
			var httpStatus = response.getStatusCode();

			resultObj.body = responseBody;
			resultObj.httpCode = httpStatus;

			if(expectedHttpStatus.indexOf(httpStatus) != -1){
				resultObj.success = true;
			} else {
				gs.error(this.type + ".executeRestMessage - error not expected http status from call to endpoint: " + restMessage.getEndpoint()
						 + " \n requestBody: " + restMessage.getRequestBody()
						 + " \n requestHeader: " + JSON.strigfiy(restMessage.getRequestHeaders())
						 + " \n responseBody: " + response.getBody() 
						 + " \n responseHeader: " + response.getAllHeaders() 
						 + " \n httpStatus: " + httpStatus 
						 + " \n expectedHttpStatus: " + expectedHttpStatus);
				resultObj.success = false;
			}
		} catch(ex) {
			gs.error("[Vifor DOM RestHelper] -  " + ex.message);
		}
		return resultObj;
	},


	type: 'ViforDOM_RESTHelper'
};
