sys_script_include.script:
var ViforDOM_DomainHelper = Class.create();
ViforDOM_DomainHelper.prototype = {

	initialize: function() {
	},


	deactivateDomains: function(dataSourceSysId, domainIdArr){
		var dataSourceArr = gs.getProperty('x_viph_domain.approval_required_to_remove').split(',');
		var certificationHelper = new ViforDOM_CertificationHelper();
		if(dataSourceArr.indexOf(dataSourceSysId) != -1){
			certificationHelper.startDomainDeactivateCertification(domainIdArr);
		}else {
			this.deactivateDomainsByNames(domainIdArr);
		}
	},


	deactivateDomainsByNames: function(domainIdArr){
		var domainGr = new GlideRecord('x_viph_domain_domain');
		domainGr.addQuery('active', true);
		domainGr.addQuery('id', 'IN', domainIdArr.join(','));
		domainGr.query();
		while(domainGr.next()){
			domainGr.setValue('active', false);
			domainGr.update();
		}
	},


	getDomainSysIdByIDs: function(ids){
		var domainSysIds = [];

		var domainGr = new GlideRecord('x_viph_domain_domain');
		domainGr.addQuery('id', 'IN', ids);
		domainGr.query();

		while(domainGr.next()){
			domainSysIds.push(domainGr.getUniqueValue());
		}
		return domainSysIds;
	},





	type: 'ViforDOM_DomainHelper'
};
