sys_script_include.script:
var ViforDOM_ExternalDomainHelper = Class.create();
ViforDOM_ExternalDomainHelper.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

	isDomainBlacklisted: function(domain){

		if(!domain){
			domain = this.getParameter('sysparm_domain');
		}

		var domainGr = new GlideRecord('x_viph_domain_domain');
		domainGr.addQuery('active', true);
		domainGr.addQuery('name', domain);
		domainGr.addQuery('designation', 'blacklisted');
		domainGr.query();

		return domainGr.next() ? true : false;
	},


    type: 'ViforDOM_ExternalDomainHelper'
});
